void test_visualization_system(void) {    StatsVisualizer* viz = stats_visualizer_create();    EventStats test_stats = generate_test_stats();        viz->plot_timeline(&test_stats);    viz->plot_distribution(&test_stats);        TEST_ASSERT_NOT_NULL(viz->time_series);    TEST_ASSERT_EQUAL(TEST_DATA_POINTS, viz->time_series->count);        stats_visualizer_destroy(viz);}void test_data_export(void) {    DataExporter* exporter = data_exporter_create(EXPORT_FORMAT_JSON);    EventStats test_stats = generate_test_stats();        exporter->export_stats(&test_stats);        TEST_ASSERT_TRUE(file_exists("test_output.json"));    TEST_ASSERT_TRUE(validate_json_format("test_output.json"));        data_exporter_destroy(exporter);}void test_alert_system(void) {    AlertSystem* alerts = alert_system_create();    AlertRule test_rule = {        .threshold = 90.0,        .severity = ALERT_CRITICAL,        .condition = check_cpu_load    };        alerts->add_rule(test_rule);        EventMetrics test_metrics = {        .cpu_load = 95.0    };        alerts->check_metrics(&test_metrics);        TEST_ASSERT_EQUAL(1, alerts->history.alert_count);    TEST_ASSERT_EQUAL(ALERT_CRITICAL, alerts->history.last_alert.severity);        alert_system_destroy(alerts);}