void test_backup_system(void) {    BackupManager* bm = backup_manager_create(&test_config);        bm->create_backup(BACKUP_TYPE_FULL);    BackupEntry* latest = bm->backups[bm->backup_count - 1];        TEST_ASSERT_NOT_NULL(latest);    TEST_ASSERT_EQUAL(BACKUP_TYPE_FULL, latest->type);        backup_manager_destroy(bm);}void test_predefined_widgets(void) {    PerformanceGraphWidget* graph = performance_graph_widget_create();    AlertWidget* alerts = alert_widget_create();        TEST_ASSERT_NOT_NULL(graph->graph);    TEST_ASSERT_NOT_NULL(alerts->alerts);        widget_destroy((Widget*)graph);    widget_destroy((Widget*)alerts);}void test_ml_config_interface(void) {    MLConfigurationUI* ui = ml_config_ui_create(&test_model);    ModelParams test_params = create_test_params();        ui->config_interface->configure_model(&test_params);    ui->config_interface->validate_config();        TEST_ASSERT_TRUE(ui->validation_results.is_valid);        ml_config_ui_destroy(ui);}void test_memory_optimization(void) {    DashboardMemoryManager* mm = dashboard_memory_manager_create();        mm->monitor_memory();    mm->apply_optimization_strategy();        TEST_ASSERT_TRUE(mm->optimizer->used_memory < mm->optimizer->total_memory * 0.8);        dashboard_memory_manager_destroy(mm);}