void test_distributed_monitoring(void) {    DistributedMonitor* monitor = distributed_monitor_create(&test_monitor_config);        monitor->register_node(&test_node_info);    NodeReport* report = monitor->generate_node_report(test_node_info.id);        TEST_ASSERT_NOT_NULL(report);    TEST_ASSERT_TRUE(report->status == NODE_STATUS_ACTIVE);        distributed_monitor_destroy(monitor);}void test_plugin_system(void) {    PluginManager* manager = plugin_manager_create(&test_plugin_config);        manager->load_plugin("test_plugin.so");    PluginInfo* info = manager->get_plugin_info("test_plugin");        TEST_ASSERT_NOT_NULL(info);    TEST_ASSERT_TRUE(info->status == PLUGIN_STATUS_LOADED);        plugin_manager_destroy(manager);}void test_admin_dashboard(void) {    DashboardManager* dashboard = dashboard_manager_create(&test_dashboard_config);        dashboard->start_dashboard();    DashboardStats* stats = dashboard->get_statistics();        TEST_ASSERT_NOT_NULL(stats);    TEST_ASSERT_TRUE(stats->active_connections > 0);        dashboard_manager_destroy(dashboard);}void test_resource_optimization(void) {    OptimizationManager* manager = optimization_manager_create(&test_optimization_config);        manager->start_optimization();    OptimizationReport* report = manager->optimizer->generate_report();        TEST_ASSERT_NOT_NULL(report);    TEST_ASSERT_TRUE(report->optimization_score > 0.8);        optimization_manager_destroy(manager);}