void rom_manager_render(void) {    if (!window.visible) return;        gui_begin_window("ROM Sprite Manager", &window.visible);        if (gui_button("Load ROM")) {        const char* rom_path = file_dialog_open("*.nes;*.md;*.smd");        if (rom_path) {            window.current_rom = rom_extractor_create(rom_path);            if (rom_extractor_analyze(window.current_rom)) {                refresh_sprite_list();            }        }    }        if (window.current_rom) {        gui_begin_tab_bar("Platforms");                if (gui_tab_item("Sprites")) {            for (uint32_t i = 0; i < window.current_rom->sprite_count; i++) {                SpriteMetadata* sprite = &window.current_rom->sprites[i];                if (gui_selectable(sprite->name)) {                    window.callbacks.on_sprite_selected(sprite);                }            }        }                if (gui_tab_item("Tools")) {            render_sprite_tools();        }                gui_end_tab_bar();    }        gui_end_window();}